import styled from "styled-components";

const Container = styled.div`
display: flex ;
justify-content: center;
align-items: center ;
width: 100% ;
height: 100vh ;
background-color: linen ;
flex-direction:column ;
`

const InputSearch = styled.input`
color: blue;
width: 200px ;
height: 45px ;
padding-left: 10px;
margin: 10px 0px  ;
width:${({widthh })=>( widthh ? '300px': '192px')}

`

const FlexContainer = styled.div`
    display:flex ;
    align-items:center ;
    justify-content: space-between;
    width:700px ;
    margin-bottom:0px ;
`

const Button = styled.button`
height: 45px  ;
padding:0 10px ;
border-radius: 3px;
background-color: blue;
border: none;
margin-left:10px;
color:white;
:active {
transition: all 2 linear ;
transform: scale(0.8) ;
background-color: darkcyan}
`

const Divall =styled.div`
transform:scale(${({open})=>(open === true ? 1 :0)});
transition: all 0.2 s linear ;

`

export { Container, Button, InputSearch, FlexContainer,Divall}
