import React, { Component } from 'react'
import { Button, Container, Divall, FlexContainer, InputSearch } from './Todo'

export default class extends Component {
  constructor(props) {
    super(props)
    this.state = {
      data: [],
      modal: false,
      Firstname: "",
      Lastname: "",
      Address: "",
      seleted: null,
    }
  }
  onChangeAll(e) {
    this.setState({ [e.target.name]: e.target.value })

  }
  addUser() {
    this.setState({
      data: [
        ...this.state.data,
        {
          id: this.state.data.length + 1,
          Firstname: this.state.Firstname,
          Lastname: this.state.Lastname,
          Address: this.state.Address,
        }
      ],
      modal: !this.state.modal,
      Firstname: "",
      Lastname: "",
      Address: "",
    })
  }
  editUser(value) {
    this.setState({ seleted: value.id })
  }
  render() {
    const { Firstname, Lastname, Address, data } = this.state;
    console.log(Firstname, Lastname, Address, data);
    return (
      <Container>
        <FlexContainer>
          <InputSearch type={'text'} placeholder={'Search'} widthh />
          {this.state.modal === true ? <Button onClick={() => this.addUser()}>Add User</Button> :
            <Button onClick={() => this.setState({ modal: !this.state.modal })}> + New User
            </Button>}
        </FlexContainer>
        <Divall open={this.state.modal}>
          {this.state.modal === true && <FlexContainer >
            <InputSearch placeholder='Firstname' name='Firstname' value={this.state.Firstname}
              onChange={(e) => this.onChangeAll(e)} />

            <InputSearch placeholder='Lastname' name='Lastname' value={this.state.Lastname} widthh
              onChange={(e) => this.onChangeAll(e)} />
            <InputSearch placeholder='Address' name='Address' value={this.state.Address}
              onChange={(e) => this.onChangeAll(e)} />
          </FlexContainer>}
        </Divall>
        <table border={1} style={{ borderCollapse: 'collapse' }}>
          <thead>
            <tr>
              <th>Firstname </th>
              <th>Lastname </th>
              <th>Addres</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {this.state.data.map((value) => (
              <tr>
                {this.state.seleted === value.id} ?(
                <>
                  <InputSearch />
                  <InputSearch />
                  <InputSearch />
                </>):(
                  <>


                  </>
                )
                <td>   <Button rang='red  ' back='white' borderr='red'
                  onClick={() => this.setState({ data: this.state.data.filter((val) => val.id !== value.id) })}>Delete</Button>
                  {this.state.seleted === value.id ? (
                    <Button rang='green' back='white ' borderr='green' >Save</Button>) : (
                    <Button rang='orange' back='white ' borderr='orange' onClick={() => this.editUser(value)}>Edit</Button>
                  )}
                </td>
              </tr>
            ))}

            <td>{value.Firstname}</td>
            <td>{value.Lastname}</td>
            <td>{value.Address}</td>
          </tbody>
        </table>
      </Container>
    )
  }
}
