import React, { Component } from 'react'
import { data } from './mock'
export default class Todo extends Component {

    state = {
        data,
        setSelect:'null',
    }
    render() {
const InputRef=React.createRef('')
console.log(InputRef.current);
        const onChangeId = ({ target }) => {
            let res = data.filter(value => `${value.id}`.includes(target.value));
            this.setState({ data: res.length ? res : data });
        }

     console.log(this.state.setSelect, 'selects');
        return (
            <div>
                <h1>Students List</h1>
                <input ref={InputRef} onChange={onChangeId} type="text" placeholder='text' />
                <select name="name" id="" onChange={(e)=> this.state.setSelect(e.target.value)} >

                    <option value="id">Id</option>
                    <option value="name">Name</option>

                </select>
                {this.state.data.map((value) => {
                    return (<h1>
                        {value.id}-{value.name}
                    </h1>)
                })}
            </div>
        )
    }
}
