import React, { Component } from 'react'
import { Button, Container, DivAll, FlexCon, Inputsearch } from './stayle'

export default class Todo extends Component {
    constructor(props) {
        super(props)
        this.state = {
            modal: false,
            data: [],
            firstname: "",
            lastname: "",
            address: "",
            seleted: null,
        }
    }
    onChangeAll(e) {
        this.setState({ [e.target.name]: e.target.value })
    }
    addUser() {
        this.setState({
            data: [
                ...this.state.data,
                {
                    id: this.state.data.length + 1,
                    firstname: this.state.firstname,
                    lastname: this.state.lastname,
                    address: this.state.address,
                }
            ],
            modal: !this.state.modal
        })

    }
    editUsr(value) {
        this.setState({ seleted: value.id })

    }

    render() {
        const { firstname, lastname, address, data } = this.state;
        console.log(firstname, lastname, address,);
        return (
            <Container>
                {/* menu search input b-n adduser */}
                <FlexCon>
                    <Inputsearch placeholder=' Search' widthh />
                    {this.state.modal === true ?
                        <Button onClick={() => this.addUser()}>Add user</Button> : <Button onClick={() => this.setState({ modal: !this.state.modal })}>New User
                        </Button>}
                </FlexCon>
                {/* INPUT search */}
                <DivAll open={this.state.modal}>
                    {this.state.modal &&
                        <FlexCon>

                            <Inputsearch placeholder=' Firstname'
                                name="firstname"
                                value={this.state.firstname}
                                onChange={(e) => this.onChangeAll(e)} />

                            <Inputsearch placeholder=' 
                                Lastname' name="lastname"
                                value={this.state.lastname}
                                onChange={(e) => this.onChangeAll(e)} />

                            <Inputsearch placeholder=' Address'
                                name="address"
                                value={this.state.address}
                                onChange={(e) => this.onChangeAll(e)} />
                        </FlexCon>}
                </DivAll>
                <table border={1} style={{ borderCollapse: "collapse  " }}>
                    <thead>
                        <tr>
                            <th>Firstname</th>
                            <th>Lastname</th>
                            <th>Address</th>
                            <th>Action</th>

                        </tr>
                    </thead>
                    <tbody>
                        {this.state.data.map((value) => (
                            <tr>
                                {this.state.seleted === value.id ? (
                                    <>
                                        <td><Inputsearch onChange={(e)=>this.onChangeAll2(e)} value={this.state.firstname}  widthh="130px" /></td>
                                        <td><Inputsearch value={this.state.lastname}  widthh="130px" /></td>
                                        <td><Inputsearch value={this.state.address}  widthh="130px" /></td>
                                    </>) : (
                                    <>
                                        <td>{value.firstname}</td>
                                        <td>{value.lastname}</td>
                                        <td>{value.address}</td>
                                    </>
                                )}
                                <td>
                                    {/* delete button */}
                                    <Button marRigth rang='red' back='white' borderr='red'
                                        onClick={() => this.setState({ data: this.state.data.filter((val) => val.id !== value.id) })}
                                    >Delete</Button>

                                    {/* edit button */}
                                    {this.state.seleted === value.id ?
                                        <Button rang='blue' back='white' borderr='blue' >Save</Button> :
                                        <Button rang='orange' back='white' borderr='orange'
                                            onClick={() => this.editUsr(value)} >
                                            Edit
                                        </Button>

                                    }

                                </td>

                            </tr>
                        ))}
                    </tbody>
                </table>

            </Container>
        )
    }
}
