import styled from "styled-components";

const Container = styled.div`
display: flex ;
justify-content: center;
align-items: center ;
width: 100% ;
height: 100vh ;
background-color: linen ;
flex-direction:column ;
`

const InputSearch = styled.input`
color: blue;
width: 200px ;
height: 45px ;
padding-left: 10px;
margin: 10px 0px  ;
width:${({widthh })=>( widthh ? '200px':'200px')}

`

const FlexContainer = styled.div`
    display:flex ;
    align-items:center ;
    justify-content: space-between;
    width:700px ;
    margin-bottom:     0px ;
`

const Button = styled.button`
height: 45px  ;
padding:0 10px ;
border-radius: 3px;
background-color: blue;
border: none;
margin-left:10px;
color:white;

:active {
transition: all 0.5 linear ;
transform: scale(1.2) ;
background-color: darkcyan}
`

export { Container, Button, InputSearch, FlexContainer }
