import styled from "styled-components";
export const Container = styled.div`
display: flex ;
justify-content: center ;
width: 700px ;
flex-direction:column ;
`
export const Inputsearch = styled.input`
padding: 0px 10px  ;
width: 250px ;
font-size: 22px ;
height: 45px ;
color: blue ;
width: ${({widthh})=>widthh ? "280px": '200px'} ;
` 

export const  Button =styled.button`
background-color: blue;
padding: 10 5px ;
 height: 45px  ;
border: none ;
border-radius: 5px;
:active {
    transform:scale(1.1) ;
    transition: all 1s linear  ;
}
`
export const DivAll =styled.div`
transform: scale(${({open})=>open ? 1 :0});
transition`


export const FlexCon=styled.div`
display:  flex;
align-items: center ;
justify-content: space-between ;
margin-bottom: 10px ;
`
