import React, { Component } from 'react'
import { Container, TodoTbale } from './Todo'

export default class Todo extends Component {
  constructor(props) {
    super(props)
    this.state = {
      data: [
        { id: 1, todo: 'Nonushta' },
        { id: 2, todo: 'ish' },
        { id: 3, todo: 'O`yin' },
      ],
      inputValue: ""
    }
  }
  addTodo() {
    this.setState({
      data: this.state.inputValue.length >= 1 ? [
        ...this.state.data,
        {
          id: this.state.data.length + 1,
          todo: this.state.inputValue
        }
      ] : this.state.data,
      inputValue: "",
    })
  }
  render() {
    return (
      <Container>
        <input value={this.state.inputValue}
          type="text" placeholder='add Todo'
          onChange={(e) => this.setState({ inputValue: e.target.value })}/>

        <button onClick={() => this.addTodo()}>Add</button>
        <table border={1} style={{ borderCollapse: "collapse" }}>
          {this.state.data.map((value, index) => {
            return (
              <tr>
                <td>{index + 1}</td>
                <td>{value.todo}</td>
                <button onClick={() => this.setState({ data: this.state.data.filter((val) => val.id !== value.id) })}>Delete</button>
              </tr>
            )
          })}
        </table>
      </Container>


    )
  }
}
